import React, { useState, useEffect } from 'react';
import { Lead, LeadForm as LeadFormType } from '../types';
import { toast } from 'react-toastify';

interface LeadFormProps {
  lead?: Lead;
  onSubmit: (data: LeadFormType) => Promise<void>;
  onCancel: () => void;
  loading?: boolean;
  autoOpen?: boolean; // New prop to indicate if form should auto-open
}

const LeadForm: React.FC<LeadFormProps> = ({ 
  lead, 
  onSubmit, 
  onCancel, 
  loading = false,
  autoOpen = false 
}) => {
  // Parse URL query parameters
  const getUrlParams = (): Partial<LeadFormType> => {
    const urlParams = new URLSearchParams(window.location.search);
    const params: Partial<LeadFormType> = {};

    if (urlParams.get('full_name')) params.full_name = urlParams.get('full_name') || '';
    if (urlParams.get('phone')) params.phone = urlParams.get('phone') || '';
    if (urlParams.get('email')) params.email = urlParams.get('email') || '';
    if (urlParams.get('notes')) params.notes = urlParams.get('notes') || '';

    return params;
  };

  // Mock data fallback
  const getMockData = (): Partial<LeadFormType> => {
    const mockLeads = [
      {
        full_name: 'John Smith',
        phone: '07123456789',
        email: 'john.smith@example.com',
        notes: 'Interested in solar panels for residential property'
      },
      {
        full_name: 'Sarah Johnson',
        phone: '07987654321',
        email: 'sarah.johnson@email.com',
        notes: 'Looking for energy efficiency solutions'
      },
      {
        full_name: 'Michael Brown',
        phone: '07555123456',
        email: 'mike.brown@company.co.uk',
        notes: 'Commercial property owner interested in renewable energy'
      }
    ];

    // Return random mock data
    return mockLeads[Math.floor(Math.random() * mockLeads.length)];
  };

  // Initialize form data with priority: existing lead > URL params > mock data
  const initializeFormData = (): LeadFormType => {
    if (lead) {
      return {
        full_name: lead.full_name || '',
        phone: lead.phone || '',
        email: lead.email || '',
        notes: lead.notes || '',
      };
    }

    const urlParams = getUrlParams();
    const mockData = getMockData();

    return {
      full_name: urlParams.full_name || mockData.full_name || '',
      phone: urlParams.phone || mockData.phone || '',
      email: urlParams.email || mockData.email || '',
      notes: urlParams.notes || mockData.notes || '',
    };
  };

  const [formData, setFormData] = useState<LeadFormType>(initializeFormData());
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  // Update form data when props change
  useEffect(() => {
    setFormData(initializeFormData());
  }, [lead]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const validateForm = () => {
    const newErrors: { [key: string]: string } = {};

    // Required field validation
    if (!formData.full_name.trim()) {
      newErrors.full_name = 'Full name is required';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    }

    // Email format validation (only if email is provided)
    if (formData.email && formData.email.trim()) {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(formData.email.trim())) {
        newErrors.email = 'Please enter a valid email address';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      toast.error('Please fix the errors before submitting');
      return;
    }

    try {
      await onSubmit(formData);
      toast.success(lead ? 'Lead updated successfully!' : 'Lead created successfully!');
    } catch (error) {
      console.error('Form submission error:', error);
      toast.error('Failed to save lead. Please try again.');
    }
  };

  const handleCancel = () => {
    // Clear URL parameters when canceling
    const url = new URL(window.location.href);
    url.search = '';
    window.history.replaceState({}, '', url.toString());
    onCancel();
  };

  return (
    <div className="card-margav p-6">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-semibold bg-gradient-to-r from-green-600 to-blue-600 bg-clip-text text-transparent">
          {lead ? 'Update Lead' : 'Add New Lead'}
        </h3>
        {autoOpen && (
          <div className="flex items-center space-x-2 text-sm text-blue-600 bg-blue-50 px-3 py-1 rounded-full">
            <span className="w-2 h-2 bg-blue-600 rounded-full animate-pulse"></span>
            <span>Auto-opened from dialer</span>
          </div>
        )}
      </div>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="full_name" className="block text-sm font-medium text-gray-700">
            Full Name *
          </label>
          <input
            type="text"
            id="full_name"
            name="full_name"
            value={formData.full_name}
            onChange={handleChange}
            className={`mt-1 block w-full border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-green-500 focus:border-green-500 sm:text-sm transition-colors duration-200 ${
              errors.full_name ? 'border-red-300 bg-red-50' : ''
            }`}
            placeholder="Enter full name"
            disabled={loading}
            autoFocus={autoOpen}
          />
          {errors.full_name && (
            <p className="mt-1 text-sm text-red-600 flex items-center">
              <span className="mr-1">⚠️</span>
              {errors.full_name}
            </p>
          )}
        </div>

        <div>
          <label htmlFor="phone" className="block text-sm font-medium text-gray-700">
            Phone Number *
          </label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className={`mt-1 block w-full border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-green-500 focus:border-green-500 sm:text-sm transition-colors duration-200 ${
              errors.phone ? 'border-red-300 bg-red-50' : ''
            }`}
            placeholder="Enter phone number"
            disabled={loading}
          />
          {errors.phone && (
            <p className="mt-1 text-sm text-red-600 flex items-center">
              <span className="mr-1">⚠️</span>
              {errors.phone}
            </p>
          )}
        </div>

        <div>
          <label htmlFor="email" className="block text-sm font-medium text-gray-700">
            Email Address
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={`mt-1 block w-full border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-green-500 focus:border-green-500 sm:text-sm transition-colors duration-200 ${
              errors.email ? 'border-red-300 bg-red-50' : ''
            }`}
            placeholder="Enter email address (optional)"
            disabled={loading}
          />
          {errors.email && (
            <p className="mt-1 text-sm text-red-600 flex items-center">
              <span className="mr-1">⚠️</span>
              {errors.email}
            </p>
          )}
        </div>

        <div>
          <label htmlFor="notes" className="block text-sm font-medium text-gray-700">
            Notes
          </label>
          <textarea
            id="notes"
            name="notes"
            rows={3}
            value={formData.notes}
            onChange={handleChange}
            className="mt-1 block w-full border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-green-500 focus:border-green-500 sm:text-sm transition-colors duration-200"
            placeholder="Enter any additional notes (optional)"
            disabled={loading}
          />
        </div>

        <div className="flex flex-col sm:flex-row justify-end space-y-2 sm:space-y-0 sm:space-x-3 pt-6">
          <button
            type="button"
            onClick={handleCancel}
            className="w-full sm:w-auto bg-white py-3 px-6 border border-gray-300 rounded-lg shadow-sm text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 transition-colors duration-200"
            disabled={loading}
          >
            Cancel
          </button>
          <button
            type="submit"
            className="btn-margav-primary w-full sm:w-auto disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
            disabled={loading}
          >
            {loading ? (
              <div className="flex items-center justify-center">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                Saving...
              </div>
            ) : (
              lead ? 'Update Lead' : 'Create Lead'
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default LeadForm;
