import React, { useState } from 'react';
import { Lead, LeadForm as LeadFormType } from '../types';

interface LeadFormProps {
  lead?: Lead;
  onSubmit: (data: LeadFormType) => Promise<void>;
  onCancel: () => void;
  loading?: boolean;
  prepopulatedData?: {
    full_name?: string;
    phone?: string;
    email?: string;
    address?: string;
    postcode?: string;
    notes?: string;
  };
}

interface ExtendedLeadFormData {
  // Contact Information
  full_name: string;
  phone: string;
  email: string;
  address: string;
  postcode: string;
  preferred_contact_time: string;

  // Property Information
  property_ownership: string;
  property_type: string;
  number_of_bedrooms: string;
  roof_type: string;
  roof_material: string;

  // Energy Usage
  average_monthly_electricity_bill: string;
  current_energy_supplier: string;
  electric_heating_appliances: string;
  energy_details: string;

  // Timeframe & Interest
  timeframe: string;
  moving_properties_next_five_years: string;
  timeframe_details: string;

  // Notes
  notes: string;
}

const LeadForm: React.FC<LeadFormProps> = ({ lead, onSubmit, onCancel, loading = false, prepopulatedData }) => {
  const [formData, setFormData] = useState<ExtendedLeadFormData>({
    // Contact Information - prioritize prepopulated data
    full_name: prepopulatedData?.full_name || lead?.full_name || '',
    phone: prepopulatedData?.phone || lead?.phone || '',
    email: prepopulatedData?.email || lead?.email || '',
    address: prepopulatedData?.address || '',
    postcode: prepopulatedData?.postcode || '',
    preferred_contact_time: '',

    // Property Information
    property_ownership: '',
    property_type: '',
    number_of_bedrooms: '',
    roof_type: '',
    roof_material: '',

    // Energy Usage
    average_monthly_electricity_bill: '',
    current_energy_supplier: '',
    electric_heating_appliances: '',
    energy_details: '',

    // Timeframe & Interest
    timeframe: '',
    moving_properties_next_five_years: '',
    timeframe_details: '',

    // Notes
    notes: prepopulatedData?.notes || lead?.notes || '',
  });

  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const validateForm = () => {
    const newErrors: { [key: string]: string } = {};

    // Required fields
    if (!formData.full_name.trim()) {
      newErrors.full_name = 'Full name is required';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    }

    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      // Convert extended form data to basic LeadFormType for API
      const basicFormData: LeadFormType = {
        full_name: formData.full_name,
        phone: formData.phone,
        email: formData.email,
        notes: `${formData.notes}\n\n--- DETAILED LEAD INFORMATION ---\n` +
               `Address: ${formData.address}\n` +
               `Postcode: ${formData.postcode}\n` +
               `Preferred Contact Time: ${formData.preferred_contact_time}\n` +
               `Property Ownership: ${formData.property_ownership}\n` +
