import React, { useState, useEffect } from 'react';
import { Lead, LeadUpdateForm, User } from '../types';
import { leadsAPI, usersAPI } from '../api';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';

interface QualifierLeadModalProps {
  lead: Lead;
  onClose: () => void;
  onSuccess: () => void;
}

const QualifierLeadModal: React.FC<QualifierLeadModalProps> = ({
  lead,
  onClose,
  onSuccess,
}) => {
  const { user } = useAuth();

  const [formData, setFormData] = useState<LeadUpdateForm>({
    status: lead.status,
    notes: lead.notes || '',
    appointment_date: lead.appointment_date ? 
      new Date(lead.appointment_date).toISOString().slice(0, 16) : '',
    field_sales_rep: lead.field_sales_rep || null,
  });
  const [loading, setLoading] = useState(false);
  const [salesReps, setSalesReps] = useState<User[]>([]);

  useEffect(() => {
    const fetchSalesReps = async () => {
      try {
        const response = await usersAPI.getUsers();
        setSalesReps(response.results.filter(user => user.role === 'salesrep'));
      } catch (error) {
        console.error('Failed to fetch sales reps:', error);
        toast.error('Failed to fetch sales representatives.');
      }
    };
    fetchSalesReps();
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      setLoading(true);

      // Debug: Log the form data being sent
      console.log('Form data being sent:', formData);

      // Clean up form data before sending
      const cleanFormData = {
        ...formData,
        appointment_date: formData.appointment_date || null,
        field_sales_rep: formData.field_sales_rep || null,
      };

      console.log('Clean form data:', cleanFormData);

      // If setting an appointment, create Google Calendar event
      if (formData.status === 'appointment_set' && formData.appointment_date) {
        try {
          // TODO: Replace with actual Google Calendar API integration
          // For now, we'll simulate the calendar event creation
          const calendarEventId = `event_${lead.id}_${Date.now()}`;
          cleanFormData.google_calendar_event_id = calendarEventId;

          // Simulate Google Calendar API call
          console.log('Creating Google Calendar event for:', {
            lead: lead.full_name,
            date: formData.appointment_date,
            salesRep: formData.field_sales_rep,
            calendar: 'ella@margav.energy'
          });

          // In a real implementation, this would call the Google Calendar API
          // await createGoogleCalendarEvent({
          //   summary: `Appointment with ${lead.full_name}`,
          //   start: new Date(formData.appointment_date),
          //   end: new Date(new Date(formData.appointment_date).getTime() + 60 * 60 * 1000), // 1 hour
          //   attendees: [{ email: 'ella@margav.energy' }],
          //   description: `Sales appointment for ${lead.full_name}. Phone: ${lead.phone}`
          // });

          toast.success('Google Calendar event created successfully!');
        } catch (error) {
          console.error('Failed to create Google Calendar event:', error);
          toast.warning('Lead updated but Google Calendar event creation failed');
        }
      }

      // Update the lead with qualification data
      await leadsAPI.qualifyLead(lead.id, cleanFormData);

      toast.success('Lead qualified successfully!');
      onSuccess();
    } catch (error) {
      console.error('Failed to qualify lead:', error);
      if (error && typeof error === 'object' && 'response' in error) {
        console.error('Error details:', (error as any).response?.data);
      }
      toast.error('Failed to qualify lead');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    console.log('Form field changed:', { name, value });
    setFormData(prev => {
      const newData = {
        ...prev,
        [name]: name === 'field_sales_rep' ? (value ? parseInt(value) : null) : value,
      };
      console.log('Updated form data:', newData);
      return newData;
    });
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-full max-w-2xl shadow-lg rounded-md bg-white">
        <div className="mt-3">
          {/* Header */}
          <div className="flex items-center justify-between mb-6">
            <div>
              <h3 className="text-2xl font-bold text-gray-900">
                Qualify Lead - {lead.full_name}
              </h3>
              <p className="text-sm text-gray-600 mt-1">
                Review and qualify lead from {lead.assigned_agent_name}
              </p>
              <p className="text-xs text-gray-500 mt-1">
                Qualifier: {user?.first_name} {user?.last_name}
              </p>
            </div>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600 text-2xl"
            >
              ×
            </button>
          </div>

          {/* Lead Information Summary */}
          <div className="bg-gray-50 rounded-lg p-4 mb-6">
            <h4 className="font-semibold text-gray-900 mb-3">Lead Information</h4>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="font-medium text-gray-700">Name:</span> {lead.full_name}
              </div>
              <div>
                <span className="font-medium text-gray-700">Phone:</span> {lead.phone}
              </div>
              <div>
                <span className="font-medium text-gray-700">Email:</span> {lead.email || 'N/A'}
              </div>
              <div>
                <span className="font-medium text-gray-700">Agent:</span> {lead.assigned_agent_name}
              </div>
            </div>
            {lead.notes && (
              <div className="mt-3">
                <span className="font-medium text-gray-700">Lead Sheet Details:</span>
                <div className="mt-1 text-sm text-gray-600 bg-white p-2 rounded border max-h-32 overflow-y-auto">
                  <pre className="whitespace-pre-wrap">{lead.notes}</pre>
                </div>
              </div>
            )}
          </div>

          {/* Qualification Form */}
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Status */}
              <div>
                <label htmlFor="status" className="block text-sm font-medium text-gray-700 mb-1">
                  Qualification Status *
                </label>
                <select
                  id="status"
                  name="status"
                  value={formData.status}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                >
                  <option value="qualified">✅ Qualified</option>
                  <option value="appointment_set">📅 Set Appointment</option>
                  <option value="not_interested">❌ Not Qualified</option>
                </select>
              </div>

              {/* Field Sales Rep */}
              {formData.status === 'appointment_set' && (
                <div>
                  <label htmlFor="field_sales_rep" className="block text-sm font-medium text-gray-700 mb-1">
                    Assign Field Sales Rep
                  </label>
                  <select
                    id="field_sales_rep"
                    name="field_sales_rep"
                    value={formData.field_sales_rep || ''}
                    onChange={handleChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                  >
                    <option value="">Select sales rep...</option>
                    {salesReps.map(rep => (
                      <option key={rep.id} value={rep.id}>
                        {rep.first_name} {rep.last_name} ({rep.username})
                      </option>
                    ))}
                  </select>
                </div>
              )}
            </div>

            {/* Appointment Date */}
            {formData.status === 'appointment_set' && (
              <div>
                <label htmlFor="appointment_date" className="block text-sm font-medium text-gray-700 mb-1">
                  Schedule Appointment
                </label>
                  <input
                    type="datetime-local"
                    id="appointment_date"
                    name="appointment_date"
                    value={formData.appointment_date || ''}
                    onChange={handleChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                  />
              </div>
            )}

            {/* Qualification Notes */}
            <div>
              <label htmlFor="notes" className="block text-sm font-medium text-gray-700 mb-1">
                Qualification Notes
              </label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                rows={3}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
                placeholder="Add notes about the qualification process..."
              />
            </div>

            {/* Action Buttons */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onClose}
                className="px-6 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
                disabled={loading}
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={loading}
                className="btn-margav-primary px-6 py-2 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? (
                  <div className="flex items-center">
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Qualifying...
                  </div>
                ) : (
                  'Qualify Lead'
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default QualifierLeadModal;
