import React from 'react';
import { useAuth } from '../contexts/AuthContext';
import AgentDashboard from './AgentDashboard';
import QualifierDashboard from './QualifierDashboard';
import ColdCallDashboard from './ColdCallDashboard';
import AdminDialerControl from './AdminDialerControl';
import KanbanBoard from './KanbanBoard';

const Dashboard: React.FC = () => {
  const { user } = useAuth();

  if (!user) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  // Render appropriate dashboard based on user role
  switch (user.role) {
    case 'agent':
      return <AgentDashboard />;
    case 'qualifier':
      return (
        <div className="space-y-6">
          <QualifierDashboard />
          <KanbanBoard userRole={user.role} />
        </div>
      );
    case 'salesrep':
      return (
        <div className="space-y-6">
          <div className="card-margav p-6">
            <h2 className="text-3xl font-bold bg-gradient-to-r from-green-600 to-blue-600 bg-clip-text text-transparent">
              Field Sales Rep Dashboard
            </h2>
            <p className="text-gray-600 mt-2">
              Welcome, {user.first_name}! This dashboard is for field sales representatives.
            </p>
            <p className="text-gray-500 mt-4">
              Field sales rep functionality is not fully implemented in this demo. 
              In a full implementation, this would show scheduled appointments, 
              allow appointment completion, and record sale results.
            </p>
          </div>
          <KanbanBoard userRole={user.role} />
        </div>
      );
    case 'admin':
      return (
        <div className="space-y-6">
          <div className="card-margav p-6">
            <h2 className="text-3xl font-bold bg-gradient-to-r from-green-600 to-blue-600 bg-clip-text text-transparent">
              Admin Dashboard
            </h2>
            <p className="text-gray-600 mt-2">
              Welcome, {user.first_name}! Control the dialer system and manage the CRM.
            </p>
          </div>
          <AdminDialerControl />
          <KanbanBoard userRole={user.role} />
        </div>
      );
    default:
      return (
        <div className="space-y-6">
          <div className="card-margav p-6">
            <h2 className="text-3xl font-bold bg-gradient-to-r from-green-600 to-blue-600 bg-clip-text text-transparent">
              Dashboard
            </h2>
            <p className="text-gray-600 mt-2">
              Welcome, {user.first_name}! Your role: {user.role}
            </p>
            <p className="text-gray-500 mt-4">
              No specific dashboard available for your role.
            </p>
          </div>
        </div>
      );
  }
};

export default Dashboard;
