import React from 'react';
import { useAuth } from '../contexts/AuthContext';
import AgentDashboard from './AgentDashboard';
import QualifierDashboard from './QualifierDashboard';
import ColdCallDashboard from './ColdCallDashboard';
import AdminDialerControl from './AdminDialerControl';

const Dashboard: React.FC = () => {
  const { user } = useAuth();

  if (!user) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  // Render appropriate dashboard based on user role
  switch (user.role) {
    case 'agent':
      return <AgentDashboard />;
    case 'qualifier':
      return <QualifierDashboard />;
    case 'salesrep':
      return (
        <div className="space-y-6">
          <div className="bg-white shadow rounded-lg p-6">
            <h2 className="text-2xl font-bold text-gray-900">Sales Rep Dashboard</h2>
            <p className="text-gray-600 mt-2">
              Welcome, {user.first_name}! This dashboard is for sales representatives.
            </p>
            <p className="text-gray-500 mt-4">
              Sales rep functionality is not implemented in this demo. 
              In a full implementation, this would show leads with scheduled appointments.
            </p>
          </div>
        </div>
      );
    case 'admin':
      return (
        <div className="space-y-6">
          <div className="bg-white shadow rounded-lg p-6">
            <h2 className="text-2xl font-bold text-gray-900">Admin Dashboard</h2>
            <p className="text-gray-600 mt-2">
              Welcome, {user.first_name}! This is the admin dashboard.
            </p>
            <p className="text-gray-500 mt-4">
              Admin functionality is not implemented in this demo. 
              In a full implementation, this would show system overview, user management, and analytics.
            </p>
          </div>
        </div>
      );
    default:
      return (
        <div className="space-y-6">
          <div className="bg-white shadow rounded-lg p-6">
            <h2 className="text-2xl font-bold text-gray-900">Dashboard</h2>
            <p className="text-gray-600 mt-2">
              Welcome, {user.first_name}! Your role: {user.role}
            </p>
            <p className="text-gray-500 mt-4">
              No specific dashboard available for your role.
            </p>
          </div>
        </div>
      );
  }
};

export default Dashboard;
