import React, { useState, useEffect } from 'react';
import { thunderboltAPI } from '../api';
import { toast } from 'react-toastify';

interface ThunderboltStats {
  total_users: number;
  active_agents: number;
  total_calls: number;
  success_rate: number;
  last_updated: string | null;
  source: string;
  status: string;
}

interface ThunderboltDashboardProps {
  className?: string;
}

const ThunderboltDashboard: React.FC<ThunderboltDashboardProps> = ({ className = '' }) => {
  const [stats, setStats] = useState<ThunderboltStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchThunderboltData();
  }, []);

  const fetchThunderboltData = async () => {
    try {
      setLoading(true);
      setError(null);

      const data = await thunderboltAPI.getDashboardData();
      setStats(data);
    } catch (error) {
      console.error('Failed to fetch Thunderbolt data:', error);
      setError('Failed to fetch Thunderbolt data');
      toast.error('Unable to connect to Thunderbolt system');
    } finally {
      setLoading(false);
    }
  };

  const handleSyncUsers = async () => {
    try {
      setLoading(true);
      await thunderboltAPI.syncUsers();
      toast.success('Users synced successfully from Thunderbolt');
      fetchThunderboltData(); // Refresh data
    } catch (error) {
      console.error('Failed to sync users:', error);
      toast.error('Failed to sync users from Thunderbolt');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className={`card-margav p-6 ${className}`}>
        <div className="flex items-center justify-center h-32">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-600"></div>
          <span className="ml-3 text-gray-600">Loading Thunderbolt data...</span>
        </div>
      </div>
    );
  }

  if (error || !stats) {
    return (
      <div className={`card-margav p-6 ${className}`}>
        <div className="text-center">
          <div className="text-red-500 text-4xl mb-4">⚠️</div>
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Thunderbolt Connection Error</h3>
          <p className="text-gray-600 mb-4">
            Unable to connect to Thunderbolt admin dashboard at 77.68.78.43
          </p>
          <button
            onClick={fetchThunderboltData}
            className="btn-margav-secondary"
          >
            Retry Connection
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className={`card-margav p-6 ${className}`}>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
            Thunderbolt Integration
          </h2>
          <p className="text-gray-600 text-sm">
            Data source: {stats.source} • Last updated: {stats.last_updated || 'Never'}
          </p>
        </div>
        <div className="flex space-x-2">
          <button
            onClick={fetchThunderboltData}
            className="btn-margav-secondary text-sm px-4 py-2"
            disabled={loading}
          >
            Refresh
          </button>
          <button
            onClick={handleSyncUsers}
            className="btn-margav-primary text-sm px-4 py-2"
            disabled={loading}
          >
            Sync Users
          </button>
        </div>
      </div>

      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
        <div className="bg-gradient-to-r from-blue-50 to-blue-100 rounded-lg p-4">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <div className="w-10 h-10 bg-blue-500 rounded-full flex items-center justify-center">
                <span className="text-white text-lg font-bold">👥</span>
              </div>
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium text-gray-600">Total Users</p>
              <p className="text-2xl font-bold text-gray-900">{stats.total_users}</p>
            </div>
          </div>
        </div>

        <div className="bg-gradient-to-r from-green-50 to-green-100 rounded-lg p-4">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <div className="w-10 h-10 bg-green-500 rounded-full flex items-center justify-center">
                <span className="text-white text-lg font-bold">🎯</span>
              </div>
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium text-gray-600">Active Agents</p>
              <p className="text-2xl font-bold text-gray-900">{stats.active_agents}</p>
            </div>
          </div>
        </div>

        <div className="bg-gradient-to-r from-purple-50 to-purple-100 rounded-lg p-4">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <div className="w-10 h-10 bg-purple-500 rounded-full flex items-center justify-center">
                <span className="text-white text-lg font-bold">📞</span>
              </div>
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium text-gray-600">Total Calls</p>
              <p className="text-2xl font-bold text-gray-900">{stats.total_calls}</p>
            </div>
          </div>
        </div>

        <div className="bg-gradient-to-r from-orange-50 to-orange-100 rounded-lg p-4">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <div className="w-10 h-10 bg-orange-500 rounded-full flex items-center justify-center">
                <span className="text-white text-lg font-bold">📊</span>
              </div>
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium text-gray-600">Success Rate</p>
              <p className="text-2xl font-bold text-gray-900">{stats.success_rate}%</p>
            </div>
          </div>
        </div>
      </div>

      {stats.status === 'error' && (
        <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-lg">
          <div className="flex items-center">
            <div className="text-red-500 text-xl mr-3">⚠️</div>
            <div>
              <h4 className="text-sm font-medium text-red-800">Connection Issue</h4>
              <p className="text-sm text-red-600 mt-1">
                Thunderbolt API is not accessible. Please check the connection settings.
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ThunderboltDashboard;
