import React, { useState, useEffect } from 'react';
import { Lead, LeadUpdateForm } from '../types';
import { leadsAPI } from '../api';
import { toast } from 'react-toastify';
import LeadCard from './LeadCard';
import QualifierLeadModal from './QualifierLeadModal';

const QualifierDashboard: React.FC = () => {
  const [leads, setLeads] = useState<Lead[]>([]);
  const [loading, setLoading] = useState(true);
  const [updatingLead, setUpdatingLead] = useState<Lead | null>(null);
  const [updateLoading, setUpdateLoading] = useState(false);

  useEffect(() => {
    fetchLeads();
  }, []);

  const fetchLeads = async () => {
    try {
      setLoading(true);
      const response = await leadsAPI.getLeads({ status: 'sent_to_kelly' });
      setLeads(response.results);
    } catch (error) {
      console.error('Failed to fetch leads:', error);
      toast.error('Failed to fetch leads');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateLead = async (lead: Lead) => {
    setUpdatingLead(lead);
  };

  const handleLeadUpdate = async () => {
    try {
      setUpdateLoading(true);

      // Remove the lead from the list after successful qualification
      if (updatingLead) {
        setLeads(prev => prev.filter(lead => lead.id !== updatingLead.id));
      }

      setUpdatingLead(null);
    } catch (error) {
      console.error('Failed to qualify lead:', error);
      toast.error('Failed to qualify lead');
    } finally {
      setUpdateLoading(false);
    }
  };

  const getStatusCounts = () => {
    const counts = {
      cold_call: 0,
      interested: 0,
      qualified: 0,
      appointment_set: 0,
      not_interested: 0,
      tenant: 0,
      other_disposition: 0,
      sent_to_kelly: 0,
      appointment_completed: 0,
      sale_made: 0,
      sale_lost: 0,
      no_contact: 0,
      blow_out: 0,
      callback: 0,
      pass_back_to_agent: 0,
    };

    leads.forEach(lead => {
      if (lead.status in counts) {
        counts[lead.status as keyof typeof counts]++;
      }
    });

    return counts;
  };

  const statusCounts = getStatusCounts();

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white shadow rounded-lg p-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-3xl font-bold bg-gradient-to-r from-green-600 to-blue-600 bg-clip-text text-transparent">
              Kelly's Qualification Dashboard
            </h2>
            <p className="text-gray-600">Review and qualify leads sent from agents</p>
          </div>
          <button
            onClick={fetchLeads}
