import React, { useState } from 'react';
import { Lead } from '../types';
import { leadsAPI } from '../api';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';

interface InterestedLeadModalProps {
  lead: Lead;
  onClose: () => void;
  onSuccess: () => void;
}

interface LeadSheetForm {
  // Contact Information
  full_name: string;
  address: string;
  postcode: string;
  phone: string;
  email: string;
  preferred_contact_time: string;

  // Property Information
  property_ownership: string;
  property_type: string;
  bedrooms: string;
  roof_type: string;
  roof_material: string;

  // Energy Usage
  monthly_electricity_bill: string;
  current_energy_supplier: string;
  electric_heating_appliances: string;
  electric_heating_details: string;

  // Timeframe & Interest
  timeframe: string;
  timeframe_details: string;
  moving_properties: string;

  // Notes
  notes: string;
}

const InterestedLeadModal: React.FC<InterestedLeadModalProps> = ({
  lead,
  onClose,
  onSuccess,
}) => {
  const { user } = useAuth();

  const [formData, setFormData] = useState<LeadSheetForm>({
    // Contact Information
    full_name: lead.full_name || '',
    address: '',
    postcode: '',
    phone: lead.phone || '',
    email: lead.email || '',
    preferred_contact_time: '',

    // Property Information
    property_ownership: '',
    property_type: '',
    bedrooms: '',
    roof_type: '',
    roof_material: '',

    // Energy Usage
    monthly_electricity_bill: '',
    current_energy_supplier: '',
    electric_heating_appliances: '',
    electric_heating_details: '',

    // Timeframe & Interest
    timeframe: '',
    timeframe_details: '',
    moving_properties: '',

    // Notes
    notes: lead.notes || '',
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      setLoading(true);

      // Format the lead sheet data into notes
      const leadSheetData = `
LEAD SHEET INFORMATION:
Agent: ${user?.first_name} ${user?.last_name}

CONTACT INFORMATION:
Full Name: ${formData.full_name}
Address: ${formData.address}
Postcode: ${formData.postcode}
Phone: ${formData.phone}
Email: ${formData.email}
Preferred Contact Time: ${formData.preferred_contact_time}

PROPERTY INFORMATION:
Property Ownership: ${formData.property_ownership}
Property Type: ${formData.property_type}
Bedrooms: ${formData.bedrooms}
Roof Type: ${formData.roof_type}
Roof Material: ${formData.roof_material}

ENERGY USAGE:
Monthly Electricity Bill: £${formData.monthly_electricity_bill}
Current Energy Supplier: ${formData.current_energy_supplier}
Electric Heating/Appliances: ${formData.electric_heating_appliances}
${formData.electric_heating_details ? `Details: ${formData.electric_heating_details}` : ''}

TIMEFRAME & INTEREST:
Timeframe: ${formData.timeframe}
${formData.timeframe_details ? `Details: ${formData.timeframe_details}` : ''}
Moving Properties: ${formData.moving_properties}

ADDITIONAL NOTES:
${formData.notes}
      `.trim();

      // First, update the lead with the lead sheet information
      await leadsAPI.updateLead(lead.id, {
        full_name: formData.full_name,
        phone: formData.phone,
        email: formData.email,
        notes: leadSheetData,
        status: 'interested',
      });

      // Then send to Kelly
      await leadsAPI.sendToKelly(lead.id);

      toast.success('Lead sheet completed and sent to Kelly for qualification!');
      onSuccess();
    } catch (error) {
      console.error('Failed to update lead:', error);
      toast.error('Failed to submit lead sheet');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-full max-w-2xl shadow-lg rounded-md bg-white">
        <div className="mt-3">
          {/* Header */}
          <div className="flex items-center justify-between mb-6">
            <div>
              <h3 className="text-2xl font-bold text-gray-900">
                Lead Interested - Collect Details
              </h3>
              <p className="text-sm text-gray-600 mt-1">
                Fill in the lead information and send to Kelly for qualification
              </p>
            </div>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600 text-2xl"
            >
              ×
            </button>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Full Name */}
              <div>
                <label htmlFor="full_name" className="block text-sm font-medium text-gray-700 mb-2">
                  Full Name *
                </label>
                <input
                  type="text"
                  id="full_name"
                  name="full_name"
                  value={formData.full_name}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  placeholder="Enter full name"
                />
              </div>

              {/* Phone */}
              <div>
                <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-2">
                  Phone Number *
                </label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  placeholder="Enter phone number"
                />
              </div>

              {/* Email */}
              <div className="md:col-span-2">
                <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
                  Email Address
                </label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  placeholder="Enter email address"
                />
              </div>

              {/* Notes */}
              <div className="md:col-span-2">
                <label htmlFor="notes" className="block text-sm font-medium text-gray-700 mb-2">
                  Lead Notes & Details
                </label>
                <textarea
                  id="notes"
                  name="notes"
                  value={formData.notes}
                  onChange={handleChange}
                  rows={4}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  placeholder="Add notes about the lead, their interest level, requirements, etc."
                />
              </div>
            </div>

            {/* Additional Information Section */}
            <div className="border-t pt-6">
              <h4 className="text-lg font-medium text-gray-900 mb-4">Call Details</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h5 className="font-medium text-gray-700 mb-2">Interest Level</h5>
                  <p className="text-sm text-gray-600">High - Ready to proceed</p>
                </div>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h5 className="font-medium text-gray-700 mb-2">Next Steps</h5>
                  <p className="text-sm text-gray-600">Send to Kelly for qualification</p>
                </div>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="flex justify-end space-x-3 pt-6 border-t">
              <button
                type="button"
                onClick={onClose}
                className="px-6 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
                disabled={loading}
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={loading}
                className="btn-margav-primary px-6 py-2 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? (
                  <div className="flex items-center">
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Sending...
                  </div>
                ) : (
                  'Send to Kelly'
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default InterestedLeadModal;
