import React from 'react';
import { Lead } from '../types';

interface LeadCardProps {
  lead: Lead;
  onUpdate?: (lead: Lead) => void;
  showActions?: boolean;
}

const LeadCard: React.FC<LeadCardProps> = ({ lead, onUpdate, showActions = true }) => {
  const getStatusColor = (status: string) => {
    const colors: { [key: string]: string } = {
      'interested': 'bg-green-100 text-green-800',
      'not_interested': 'bg-red-100 text-red-800',
      'qualified': 'bg-blue-100 text-blue-800',
      'appointment_set': 'bg-purple-100 text-purple-800',
    };
    return colors[status] || 'bg-gray-100 text-gray-800';
  };

  const getStatusDisplayName = (status: string) => {
    const names: { [key: string]: string } = {
      'interested': 'Interested',
      'not_interested': 'Not Interested',
      'qualified': 'Qualified',
      'appointment_set': 'Appointment Set',
    };
    return names[status] || status;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  return (
    <div className="card-margav p-6 hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
        <div className="flex items-center justify-between">
          <div className="flex-1">
            <h3 className="text-lg font-medium text-gray-900">{lead.full_name}</h3>
            <div className="mt-1 flex items-center space-x-4 text-sm text-gray-500">
              <span>{lead.phone}</span>
              {lead.email && <span>{lead.email}</span>}
            </div>
          </div>
          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(lead.status)}`}>
            {getStatusDisplayName(lead.status)}
          </span>
        </div>

        {lead.notes && (
          <div className="mt-3">
            <p className="text-sm text-gray-600">{lead.notes}</p>
          </div>
        )}

        <div className="mt-4 flex items-center justify-between text-sm text-gray-500">
          <div>
            <span>Assigned to: </span>
            <span className="font-medium">{lead.assigned_agent_name || lead.assigned_agent_username}</span>
          </div>
          <div>
            Created: {formatDate(lead.created_at)}
          </div>
        </div>

        {lead.appointment_date && (
          <div className="mt-2 text-sm text-indigo-600">
            <span className="font-medium">Appointment: </span>
            {formatDate(lead.appointment_date)}
          </div>
        )}

        {showActions && onUpdate && (
          <div className="mt-6 flex space-x-2">
            <button
              onClick={() => onUpdate(lead)}
              className="btn-margav-secondary text-sm px-4 py-2"
            >
              Update
            </button>
          </div>
        )}
    </div>
  );
};

export default LeadCard;
